mapper.select:
SELECT *
FROM badges

WHERE badge_name like concat('%',#{keyword},'%');

SELECT *
FROM stats_theme

WHERE stats_theme_name like concat('%',#{keyword},'%');

SELECT *
FROM hits

WHERE hits_name like concat('%',#{keyword},'%');

SELECT count(*)
FROM board
WHERE board_id = #{keyword};

mapper.delete:
DELETE
FROM board
WHERE user_id = #{user_id} and write_date is null;
